class Solution
{
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>> &queries)
    {
        vector<int> ans;
        vector<int> dist(n, 0);

        for (int i = 0; i < n - 1; i++) dist[i + 1] = dist[i] + 1;
        set<int> st; for (int i = 0; i < n; i++) st.insert(i);


        for (auto &i : queries)
        {
            auto lb = st.upper_bound(i[0]);
            auto ub = st.lower_bound(i[1]);

            st.erase(lb, ub);
            ans.push_back(size(st) - 1);
        }

        return ans;
    }
};
