class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        import bisect
        lists = [[i, i + 1] for i in range(n - 1)]
        rst = []
        for a, b in queries:
            k = bisect.bisect_left(lists, a, key=lambda line: line[0])
            if k < len(lists) and lists[k][0] == a:
                if lists[k][1] < b:
                    lists[k] = [a, b]
                    k += 1
                    while k < len(lists) and lists[k][0] < b:
                        del(lists[k])
            rst.append(len(lists))
        return rst
