from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        s = SortedList()
        for i in range(n):
            s.add(i)
        ret = []
        for u,v in queries:
            l = s.bisect_left(u)
            r = s.bisect_left(v)
            # print(u, v, s, l, r)
            if s[l] == u and s[r] == v:
                while s[l+1] != v:
                    s.pop(l+1)
            # print(s)
            ret.append(len(s)-1)
        return ret
