class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries)
    {
        set<int> status;
        for (int i = 0; i < n; i++)
            status.insert(i);

        vector<int> ans;
        for (auto &query : queries)
        {
            int x = query[0], y = query[1];
            if (status.find(x) != status.end() && status.find(y) != status.end())
            {
                auto ptr = status.find(x);
                ptr++;

                vector<int> delVec;
                while (ptr != status.end() && *ptr < y)
                {
                    delVec.push_back(*ptr);
                    ptr++;
                }

                for (int ele : delVec)
                    status.erase(status.find(ele));
            }

            // for (auto &u : status)
            //     cout << u << "\t";
            // cout << endl;
            ans.push_back(status.size() - 1);
        }

        return ans;
    }
};
