from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SortedList()
        mmap = {}
        dist = n-1
        res = []
        for (a,b) in queries:
            if a in mmap and mmap[a] > b:
                res.append(dist)
                continue
            pos = sl.bisect_left(a)
            if pos > 0 and mmap[sl[pos-1]] >= b:
                res.append(dist)
                continue

            dist -= (b-a)-1
            while pos < len(sl):
                start = sl[pos]
                if start >= b:
                    break
                dist += (mmap[start] - start)-1
                sl.pop(pos)
            sl.add(a)
            mmap[a] = b
            res.append(dist)
        return res
