class LL {
public:
    int val;
    LL *next;
    LL(int value): val(value), next(nullptr) {}
};
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        LL *ph = new LL(-1);
        map<int, LL*> pos;
        LL *res = ph;
        for (int i = 0; i < n; i++) {
            res->next = new LL(i);
            res = res->next;
            pos[i] = res;
        }

        vector<int> ans;
        for (auto q: queries) {
            int s = q[0], d = q[1];
            if (s < d && pos.find(s) != pos.end() && pos.find(d) != pos.end()) {
                LL* source = pos[s];
                LL* cur = source;
                while (cur->next != nullptr && cur->next->val < d) {
                    cur = cur->next;
                    pos.erase(cur->val);
                }
                cur = cur->next;
                source->next = cur;
            }
            ans.push_back(pos.size()-1);
        }
        return ans;
    }
};
