class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int m = queries.length;
        int[] ans = new int[m];
        TreeSet<Integer> set = new TreeSet<>();
        for (int i = 0; i < n; i++) {
            set.add(i);
        }
        for (int i = 0; i < m; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            Integer ceiling = set.ceiling(l + 1);
            while (ceiling != null && ceiling < r) {
                set.remove(ceiling);
                ceiling = set.ceiling(l + 1);
            }
            ans[i] = set.size() - 1;
        }
        return ans;
    }
}
