class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> v(n),ans;
        for(int i=0;i<n;i++) v[i]=i;
        for(int i=0;i<q.size();i++){
            int start=0,end=v.size()-1,ind=-1;
            while(start<=end){
                int mid=(start+end)/2;
                if(v[mid]>q[i][0]){
                    end=mid-1;
                }
                else if(v[mid]==q[i][0]){
                    ind=mid;
                    break;
                }
                else start=mid+1; 
            }
            if(ind!=-1){
                int k=ind+1;
                while(k<v.size()){
                    if(v[k]>=q[i][1]) break;
                    v.erase(v.begin()+k);
                }
            }
            ans.push_back(v.size()-1);
        }
        return ans;
    }
};
