class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        int curdis=n-1;
        map<int,int> mp;
        for(int i=0;i<n-1;i++){
            mp[i]=i+1;
        }
        for(auto q:queries){
            int u=q[0],v=q[1];
            int dis=0;
            for(int i=u;i<v;){

                if(mp[i]!=-1){
                    dis=dis+1;
                    int ii=mp[i];
                    mp[i]=-1;
                    i=ii;
                }
                else{
                    dis=-1;
                    break;
                }

            }
            if(dis==-1){
                ans.push_back(curdis);
                continue;
            }
            mp[u]=v;
            curdis=curdis-dis+1;
            ans.push_back(curdis);

        }
        return ans;

    }
};
