class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeSet<Integer> set = new TreeSet<>();
        for(int i = 0; i < n; ++i) {
            set.add(i);
        }
        int m = queries.length;
        int[] ans = new int[m];
        for(int i = 0; i < m; ++i) {
            int x = queries[i][0], y = queries[i][1];
            set.removeAll(new ArrayList<>(set.subSet(x + 1, y)));
            ans[i] = set.size() - 1;
        }
        return ans;
    }
}
