class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m = q.size();
        vector<int> d(n), ans(m);
        iota(d.begin(), d.end(), 0);
        set<int> g(d.begin(), d.end());
        for (int i = 0; i < m; ++i) {
            int a = q[i][0], b = q[i][1];
            if (g.count(a) && g.count(b)) {
                g.erase(g.find(a), g.find(b));
                g.insert(a);
            }
            ans[i] = g.size()-1;
        }
        return ans;
    }
};
