class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> record;
        vector<int> res;

        for(int i = 0; i + 1 < n; ++i) record[i] = i + 1;

        for(auto &query : queries) {
            auto it = record.lower_bound(query[0]);
            bool flag = false;
            while(it != record.end() && it->first >= query[0] && it->second <= query[1]) {
                it = record.erase(it);
                flag = true;
            }

            if(flag) record[query[0]] = query[1];

            res.push_back(record.size());
        }

        return res;
    }
};
