from sortedcontainers import SortedList as sl
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:

        arr=sl([i for i in range(n)])


        def binary_s(key):
            le=len(arr)
            low=0; high =le-1

            while low<=high:
                mid=(low+high)//2
                if arr[mid]==key:
                    return mid
                elif arr[mid]<key:
                    low=mid+1
                else:
                    high=mid-1
            return -1


        result=[]
        for u,v in queries:
            p=binary_s(u)
            q=binary_s(v)
            if p!=-1 and q!=-1:
                parr=[arr[i] for i in range(p+1,q)]
                for ele in parr:
                    arr.discard(ele)
            result.append(len(arr)-1)
        return result
