class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> se;
        for(int i = 0; i < n; ++i)
            se.insert(i);

        vector<int> ans;
        for(auto &e : queries) {
            auto l = se.lower_bound(e[0] + 1);
            auto r = se.lower_bound(e[1]);
            se.erase(l, r);
            ans.push_back(se.size() - 1);
        }
        return ans;
    }
};
