class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int m = queries.size();
        map<int, int> path;
        for (int i = 0; i < n - 1; i++) {
            path[i] = 1;
        } 
        int res = n - 1;
        vector<int> ans(m);
        for (int i = 0; i < m; i++) {
            auto q = queries[i];
            int s = q[0], e = q[1];
            auto lp = path.lower_bound(s), rp = path.lower_bound(e);
            int dis = distance(lp, rp);
            if (dis > 1) {
                res -= dis - 1;
                path.erase(lp, rp);
                path[s] = 1;
            }
            ans[i] = res;
        }
        return ans;
    }
};
