class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int ans=n-1;
        map<int,int> m;//right,left
        vector<int> res;
        for(auto q : queries){
            int u=q[0],v=q[1];
            // cout<<u<<" "<<v<<endl;
            ans-=v-u-1;
            // m[v]=u;
            while(1){
                auto it=m.upper_bound(u);
                if(it==m.end()) break;
                int ri=(*it).first,le=(*it).second;
                // cout<<le<<" "<<ri<<endl;
                if(le>=v) break;
                if(ri>v||le<u){
                    ans+=v-u-1;
                    u=le;v=ri;
                    m.erase(it);
                    continue;
                }
                ans+=ri-le-1;
                m.erase(it);
            }
            m[v]=u;
            res.push_back(ans);
        }
        return res;
    }
};
