class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>ans;
        set<int>s;
        for(int i=0;i<n;++i) s.insert(i);
        for(auto &q:queries){
            int l=q[0],r=q[1];
            auto it=s.upper_bound(l);
            while(it!=s.end()&&(*it)<r){
                it=s.erase(it);
            }

            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
