class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        std::vector<int> next(n);
        for (int i = 0; i < n; ++i) {
            next[i] = i + 1;
        }

        std::vector<int> ans;
        int dis = n - 1;
        for (auto& query: queries) {
            int u = query[0], v = query[1];
            if (v > next[u]) {
                int d = deleteFrom(u, v, next);
                dis -= d;
                next[u] = v;
            }
            ans.push_back(dis);
        }
        // std::cout << std::endl;
        return ans;
    }
private:
    int deleteFrom(int u, int v, std::vector<int> &next) {
        // std::cout << "[debug]u: " << u << ", v: " << v << ", deleted: ";
        int cnt = 0;
        u = next[u];
        while (next[u] <= v) {
            int k = next[u];
            next[u] = next.size();
            // std::cout << u << " ";
            u = k;
            cnt++;
        }
        // std::cout << std::endl;
        return cnt;
    }
};
