class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        next = [0] * n
        value = [1] * n
        for i in range(n-1):
            next[i] = i+1
        res = []
        currCnt = n
        for q in queries:
            start = q[0]
            end = q[1]
            cur = start
            if next[start] >= end or value[start] == 0:
                res.append(currCnt - 1)
                continue
            while cur < end:

                cur = next[cur]

                if cur > start and cur < end and value[cur] == 1:
                    value[cur] = 0
                    currCnt -= 1

            next[start] = end
            res.append(currCnt - 1)

        return res
