class Solution:
    def shortestDistanceAfterQueries(
        self, n: int, queries: List[List[int]]
    ) -> List[int]:
        path_hash = {}
        for i in range(n - 1):
            path_hash[i] = i + 1
        last_minimum = n - 1
        ans = []

        def find_ans(k, j):
            ans = 0
            while k != j:
                k = path_hash[k]
                ans += 1
            return ans

        for q in queries:
            if q[0] in path_hash:
                prev_val = path_hash[q[0]]
                if q[1] > prev_val:
                    prev_dist = find_ans(q[0], q[1])
                    last_minimum=last_minimum-(prev_dist-1)
                    for i in range(prev_val, q[1]):
                        if i in path_hash:
                            del path_hash[i]
                    path_hash[q[0]] = q[1]
            ans.append(last_minimum)
        return ans
