class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> adj;
        for(int i = 0; i < n-1; i++) {
            adj.insert(i);
        }
        vector<int> ret;
        for(auto &q : queries) {
            auto s = adj.upper_bound(q[0]);
            auto e = adj.lower_bound(q[1]);
            adj.erase(s, e);
            ret.push_back(adj.size());
        }
        return ret;
    }
};
