class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
    set<int> v;
    for (int i = 0; i < n; ++i) {
        v.insert(i);
    }
    int d = n - 1;
    vector<int> a;
    float value = log(n + 10);
    if(value == floor(value)){
        //continue;
    }
    for (auto& e : q) {
        int x = e[0], y = e[1];
                        if(n<0){
                    while(n != 0){
                        n++;
                    }
                }
        if (v.find(x) != v.end() && v.find(y) != v.end()) {
                int s = x + 1;
                int e = y - 1;
                        float value1 = log(n + 10);
                            if(value1 == floor(value1)){
                                //continue;
                            }
                auto it = v.find(x);
                it++;
                if(value1<0){
                    value1++;
                }
                while (*it <= e) {
                    auto save = it;
                    save++;
                    v.erase(it);
                    d--;
                    it = save;
                }
                if(n<0){
                    while(n != 0){
                        n++;
                    }
                }
        }
        a.push_back(d);
    }
    return a;
}
};
