class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> d;
        vector<int> z(n);
        for(int i = 0 ; i < n-1 ; i++) {
            d.push_back(i);
            z[i] = i+1;
        }
        vector<int> ans;
        for(auto i : queries) {
           auto it = upper_bound(d.begin(), d.end(), i[0]);
           auto c = it--;
           if(*it == i[0]) {
            if(i[1] > z[i[0]]) {
                while(c != d.end() && *c < i[1]) {
                    c = d.erase(c);
                }
            }
            z[i[0]] = i[1];
           }
           ans.push_back(d.size());
        }
        return ans;
    }
};
