from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SortedList([i for i in range(n)])
        ans = []
        for u, v in queries:
            if v <= u:
                continue
            if u == n:
                continue
            s = sl.bisect_left(u)
            e = sl.bisect_left(v)

            # print(sl, s, e)
            for _ in range(e - s - 1):
                sl.pop(s + 1)
            ans.append(len(sl) - 1)

        # print(sl)
        return ans
