class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }
        vector<int> answer;
        for (vector<int>& q : queries) {
            auto it0 = s.find(q[0]);
            auto it1 = s.find(q[1]);
            if (it0 != s.end() && it1 != s.end()) {
                it0++;
                while (it0 != it1) {
                    it0 = s.erase(it0);
                }
            }
            answer.push_back(s.size()-1);
        }
        return answer;
    }
};
