class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int q_n=queries.size();
        vector<int> rec(n);
        vector<int> ans(q_n);
        int len=n-1;
        for(int i=0;i<q_n;++i){
            int x=queries[i][0],y=queries[i][1];
            if(rec[x]!=-1){
                int k=x+1;
                if(rec[x]!=0)
                    k=rec[x];
                while(k<y){
                    len--;
                    if(rec[k]==0){
                        rec[k]=-1;
                        k++;
                    }
                    else{
                        int t=rec[k];
                        rec[k]=-1;
                        k=t;
                    }
                }
                rec[x]=y;
            }
            ans[i]=len;
        }
        return ans;
    }
};
