class Solution {
    int nxt[100005];
    int del[100005];
    int now;
    int add(int n, int x, int y){
        if(del[x] || nxt[x]>=y) return now;
        int p = nxt[x];
        int cnt = 0;
        while(p!=y){
            // printf("%d %d %d\n", p, x, y);
            del[p] = true;
            cnt ++;
            p = nxt[p];
            // if(cnt > 100) break;
        }
        now -= cnt;
        nxt[x] = y;
        return now;
    }
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qs) {
        for(int i=0;i<n-1;i++){
            nxt[i] = i+1;
        }
        now = n-1;
        vector<int> ret;
        for(auto q: qs){
            ret.push_back(add(n, q[0], q[1]));
        }
        return ret;
    }
};
