class Solution {
    fun shortestDistanceAfterQueries(n: Int, queries: Array<IntArray>): IntArray {
        var hashSet = TreeSet<Int>()
        for (i in 0 until n) {
            hashSet.add(i)
        }
        var res = mutableListOf<Int>()
        for(i in queries) {
            if (hashSet.contains(i[0]) && hashSet.contains(i[1])) {
                while(hashSet.higher(i[0])<i[1]) {
                    hashSet.remove(hashSet.higher(i[0]))
                }
            }
            res.add(hashSet.size-1)
        }
        return res.toIntArray()
    }
}
