class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n, 0); multiset<int> arr; vector<int> res;
        for (int i = 0; i < n - 1; ++i) dist[i + 1] = dist[i] + 1;
        for (int i = 1; i <= n; ++i) arr.insert(i - 1);
        for (auto& q : queries) {
            int l = q[0], r = q[1];
            auto lb = arr.upper_bound(l), ub = arr.upper_bound(r - 1);
            arr.erase(lb, ub);
            res.push_back(size(arr) - 1);
        }
        return res;
    }
};
