class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>ans;
        map<int, int>mp;
        for(int i = 0; i < n - 1; i++){
            mp[i] = i + 1;
        }

        for(auto query : queries){
            int u = query[0];
            int v = query[1];
            if(mp.find(u) != mp.end() && v > mp[u]){
               while(mp[u] != v){
                    int j = mp[u];
                    mp[u] = mp[j];
                    mp.erase(j);
                } 
            }

            ans.push_back(mp.size());
        }
        return ans;
    }
};
