class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        vector<int> ans;
        for(int i=0; i<n; i++)
            st.insert(i);
        for(auto q:queries)
        {
            if(st.find(q[0])==st.end())
            {
                ans.push_back(st.size()-1);
                continue;
            }
            for(int i=q[0]+1; i<q[1]; i++)
            {
                if(st.find(i)!=st.end())
                    st.erase(i);
                else
                {    
                    i = *st.upper_bound(i);
                    i--;
                }
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
