#include <bits/stdc++.h>

using namespace std;

#ifdef B01
#include "deb.h"
#else
#define deb(...)
#endif

class Solution {
 public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qrs) {
    int d = n - 1;
    set<pair<int, int>> se;
    vector<int> ans;
    for (const auto& e : qrs) {
      auto it = se.lower_bound({e[0], -1});
      while (it != se.end() && it->second <= e[1]) {
        d += it->second - it->first - 1;
        deb(e, *it);
        it = se.erase(it);
      }
      it = se.lower_bound({e[0] + 1, -1});
      if (it == se.begin() || prev(it)->second <= e[0]) {
        d -= e[1] - e[0] - 1;
        se.emplace(e[0], e[1]);
      }
      ans.push_back(d);
    }
    return ans;
  }
};

#ifdef B01
int main() {
  Solution s;
  vector<vector<int>> q = {{2,4},{0,2},{0,4}};
  deb(s.shortestDistanceAfterQueries(5, q));
  q = {{0,3},{0,2}};
  deb(s.shortestDistanceAfterQueries(4, q));

}
#endif
