class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int qn = queries.length;
        int[] res = new int[qn];
        int cur = n - 1;
        int[] next = new int[n];
        for (int i = 0; i < n; i++) {
            if (i < n - 1) {
                next[i] = i + 1;
            }
        }
        for (int i = 0; i < qn; i++) {
            int u = queries[i][0];
            int v = queries[i][1];
            if (v > next[u]) {
                int p = u;
                while (p < v) {
                    p = next[p];
                    cur--;
                }
                cur++;
                for (int j = u; j < v; j++) {
                    next[j] = v;
                }

            }
            res[i] = cur;
        }
        return res;
    }
}
