class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        set<int> st;

        for(int i=0; i<n; i++) {
            st.insert(i);
        }

        for(auto q : queries) {
            int x = q[0], y = q[1];
            auto it = st.upper_bound(x);
            while(it != st.end() && *it < y) {
                it = st.erase(it);
            }
            ans.push_back(st.size()-1);
        }
     return ans;
    }
};
