class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {

         vector<int> ret;
        set<int> s;

        for(int i=0;i<n;i++){
            s.insert(i);
        }


        int ans=n;

        for(auto i:q){

            int x=i[0];
            int y=i[1];

            auto it=s.upper_bound(x);

            while(it!=s.end() && (*it)<y){

                auto er=it;
                it++;
                s.erase(er);
            }
            // for(auto i:s)cout<<i<<" ";
            // cout<<endl;

            ret.push_back(s.size()-1);

        }

        return ret;

    }
};
