class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qry) {
        int len = qry.size();
        vector<int>res;
        set<int>st;
        for(int i = 0; i < n; i++){
            st.insert(i);
        }
        for(auto q: qry){
            int x = q[0], y = q[1];
            while(true){
                auto it = st.upper_bound(x);
                if(it != st.end() && *it < y){
                    st.erase(it);
                } else {
                    break;
                }
            }
            res.push_back(st.size()-1);
        }
        return res;
    }
};
