class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> sols;
        map<int,int> next;
        for (int i = 0; i < n-1; ++i)
            next[i] = i+1;
        int time = n-1;
        for (int i = 0; i < queries.size(); ++i) {
            int from = queries[i][0];
            int to = queries[i][1];
            if (next.count(from)) {
                if (next[from] < to) {
                    int x = from;
                    int old_time = 0;
                    while (x != to) {
                        int tmp = next[x];
                        old_time++;
                        next.erase(x);
                        x = tmp;
                    }
                    next[from] = to;
                    time -= old_time - 1;
                }
            }
            sols.push_back(time);
        }
        return sols;
    }
};
