class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>s;
        for(int i=0;i<n;i++)
        s.insert(i);
        int ans=n-1;
        vector<int>fans;
        for(auto i:queries)
        {
            int u=i[0];
            int v=i[1];
            int c=0;
            auto it=s.upper_bound(u);
            while(it!=s.end()&&(*it)<v)
            {
                s.erase(it);
                it=s.upper_bound(u);
                c++;
            }
            ans-=c;
            fans.push_back(ans);
        }
        return fans;

    }
};
