class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;

        set<pair<int, int>> st; 
        for (int i = 0; i + 1 < n; i++)
            st.emplace(i, i + 1);

        for (auto v : queries) {
            int a = v[0], b = v[1];

            auto it = st.lower_bound({a, -1});
            vector<pair<int, int>> rem;
            while (it != st.end() && it->second <= b) {
                rem.push_back(*it);
                it = next(it);
            }

            for (auto p : rem)
                st.erase(p);
            if (rem.size())
                st.emplace(a, b);

            ans.push_back(st.size());
        }
        return ans;
    }
};
