class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
       vector<int>ans(n,0);
        int t=n-1;
        vector<int>dist;
        int size=queries.size();
        for(int i=0;i<size;i++)
        {
            if(ans[queries[i][0]]==-1)
            {
                dist.push_back(t);
                continue;
            }
            if(queries[i][1]-queries[i][0]>ans[queries[i][0]])
            {
                t-=(queries[i][1]-queries[i][0]-1);
                if(ans[queries[i][0]]>1)
                    t+=ans[queries[i][0]]-1;
                for(int j=queries[i][0]+ans[queries[i][0]];j<queries[i][1];j++)
                {
                    if(ans[j]>1)
                    t+=ans[j]-1;
                    ans[j]=-1;

                }
                 ans[queries[i][0]]= queries[i][1]-queries[i][0];
            }
            dist.push_back(t);
        }
        return dist;

    }
};
