class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int, int> mp;
        for (int i = 0; i < n-1; ++i) {
            mp[i] = i+1;
        }
        int *del = new int[n+1];
        for (int i = 0; i <= n; ++i) {
            del[i] = 0;
        }
        vector<int> ans = {n-1};
        for (auto&q : queries) {
            int from = q[0], to = q[1];
            if (del[from] == -1) {
                ans.emplace_back(ans.back());
                continue;
            }
            int cnt = 0;
            for(int i = mp[from]; i < to; i = mp[i]) {
                del[i] = -1;
                cnt++;
            }
            mp[from] = to;
            ans.emplace_back(ans.back()-cnt);
        }
        int t = queries.size();
        vector<int> ret;
        for (int i = 1; i <= t; ++i) {
            ret.emplace_back(ans[i]);
        }
        return ret;
    }
};
