from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = []
        sl = SortedList()
        savedDist = 0
        for u, v in queries:
            idu = sl.bisect_left((u, float('-inf')))
            while idu < len(sl) and sl[idu][1] <= v:
                a, b = sl.pop(idu)
                savedDist -= b - a - 1
            flag = True
            if idu < len(sl) and sl[idu][0] <= u:
                flag = False
            if 0 <= idu - 1 < len(sl) and sl[idu - 1][1] >= v:
                flag = False
            if flag:
                sl.add((u, v))
                savedDist += v - u - 1
            ans.append(n - 1 - savedDist)
        return ans
