class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        rem = {}
        a = [False] * (n+2)
        cur = 0
        ans = []
        for q in queries:
            if a[q[0]] or (q[0] in rem and rem[q[0]] > q[1]):
                ans.append(n-cur-1)
                continue

            idx = q[0]+1 if q[0] not in rem else rem[q[0]]
            while idx < q[1]:
                cur+=1
                a[idx] = True
                if idx in rem:
                    idx = rem[idx]
                else:
                    idx+=1

            ans.append(n-cur-1)
            rem[q[0]] = q[1]

        return ans
