class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int cur = n - 1;
        vector<int> ans;
        typedef pair<int, int> pii;
        set<pii> s;
        for (const auto& q:queries) {
            const int u = q[0], v = q[1];
            auto it1 = s.lower_bound(pii(u + 1, 0));
            if (it1 != s.begin()) {
                it1--;
                if (it1 -> second >= v) {
                    ans.push_back(cur);
                    continue;
                }
            }
            auto it2 = s.lower_bound(pii(u, 0));
            while (it2 != s.end() && it2 -> second <= v) {
                cur += it2 -> second - it2 -> first - 1;
                s.erase(it2);
                it2 = s.lower_bound(pii(u, u));
            }
            s.insert(pii(u, v));
            cur -= v - u - 1;
            ans.push_back(cur);
        }
        return ans;
    }
};
