class Solution {

    /**
     * @param Integer $n
     * @param Integer[][] $queries
     * @return Integer[]
     */
    function shortestDistanceAfterQueries($n, $queries) {
        $graph = [];
        for ($index = 0; $index < $n - 1; ++ $index) {
            $graph[$index] = $index + 1;
        }
        $ans = [];
        $count = $n - 1;
        foreach ($queries as $query) {
            $find = false;
            $index = $query[0];
            while ($index < $query[1]) {
                if ($graph[$index] === null || $graph[$index] > $query[1]) {
                    break;
                }
                $next = $graph[$index];
                unset($graph[$index]);
                $find = true;
                $index = $next;
                -- $count;
            }
            if ($find) {
                $graph[$query[0]] = $query[1];
                ++ $count;
            }
            $ans[] = $count;
        }

        return $ans;
    }
}
