class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        multiset<int> mt;
        for (int i = 0; i < n; i++) {
            mt.insert(i);
        }

        vector<int> ans;
        for (auto& q : queries) {
            int left = q[0];
            int right = q[1];
            auto it1 = mt.lower_bound(left + 1);
            auto it2 = mt.lower_bound(right);
            mt.erase(it1, it2);

            ans.push_back(mt.size() - 1);
        }

        return ans;
    }
};
