class Solution {
    vector<int> tree;
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;
        tree.resize(n*4);

        for (auto& q : queries) {
            update(0, 0, n-1, q[0]+1, q[1]-1);
            //cout<<tree[0]<<" ";
            res.push_back(n-1-tree[0]);
        }

        return res;
    }

    void update(int cur, int l, int r, int a, int b) {
        if (a>r || b<l || tree[cur]==r-l+1) return;
        if (a<=l && r<=b) {
            tree[cur]=r-l+1;
            return;
        }
        if (l>=r) {
            tree[cur]=1;
            return;
        }
        int left=cur*2+1;
        int right=cur*2+2;
        int mid=((l+r)>>1);
        update(left, l, mid, a, b);
        update(right, mid+1, r, a, b);
        tree[cur]=tree[left]+tree[right];
    }
};
