class BB {
private:
    vector<int> m_arr;
public:
    BB(int n) : m_arr(n + 1) {}

    int get_lower(int x) {
        return x & (-x);
    }

    void add(int index, int to_add) {
        while (index < m_arr.size()) {
            m_arr[index] += to_add;
            index += get_lower(index);
        }
    }

    int query(int index) {
        int res = 0;
        while (index > 0) {
            res += m_arr[index];
            index -= get_lower(index);
        }
        return res;
    }
};

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int m = queries.size();

        vector<int> res;

        BB bit(n + 100);
        vector<int> diff(n + 100, 1);
        set<int> es;
        unordered_map<int, int> e2s;
        for (int i = 2; i <= n; ++ i) bit.add(i, 1);
        for (const auto& q: queries) {
            int l = q[0] + 1, r = q[1] + 1;
            int sum = bit.query(r) - bit.query(l);
            if (sum > 1) {
                bit.add(r, - (sum - 1));
                diff[r] -= sum - 1;

                auto eit = es.upper_bound(r);
                if (eit != es.end() && e2s[*eit] <= l) {
                    bit.add(*eit, sum - 1);
                    diff[*eit] += sum - 1;
                }
            }
            res.push_back(bit.query(n));
            es.insert(r);
            e2s[r] = l;
        }
        return res;
    }
};
