from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        path = SortedList([i for i in range(n)])
        answer = []

        for u, v in queries:
            for i in list(path.irange(u, v, inclusive=(False, False))):
                path.remove(i)
            answer.append(len(path) - 1)

        return answer
