import java.util.TreeMap;

class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {

        TreeMap<Integer, Integer> map = new TreeMap<>();

        int d = n - 1;

        for (int i = 0; i + 1 < n; i++) {
            map.put(i, i + 1);
        }

        int[] ans = new int[queries.length];
        for (int i = 0; i < ans.length; i++) {
            int s = queries[i][0], t = queries[i][1];
            var floor = map.floorEntry(s);
            if (floor.getValue()>= t) {
                ans[i] = d;
                continue;
            }
            d++;
            while (floor != null && floor.getKey() < t) {
                map.remove(floor.getKey());
                d--;
                floor = map.ceilingEntry(floor.getKey());
            }
            map.put(s, t);
            ans[i] = d;
        }
        return ans;

    }
}
